function decodeFirebaseIdToken(token) {
  try {
    const [headerB64, payloadB64] = token.split('.').slice(0, 2);
    const header = JSON.parse(atob(headerB64.replace(/-/g, '+').replace(/_/g, '/').replace(/=/g, '')));
    const payload = JSON.parse(atob(payloadB64.replace(/-/g, '+').replace(/_/g, '/').replace(/=/g, '')));
    return { header, payload };
  } catch (e) {
    return { error: e.message };
  }
